mapper.select:
select
  id, passenger_id, passenger_phone, driver_id, driver_phone, car_id, vehicle_type, address, order_time, depart_time, departure, dep_longitude, dep_latitude, destination, dest_longitude, dest_latitude, encrypt, fare_type, fare_version, receive_order_car_longitude, receive_order_car_latitude, receive_order_time, license_id, vehicle_no, to_pick_up_passenger_time, to_pick_up_passenger_longitude, to_pick_up_passenger_latitude, to_pick_up_passenger_address, driver_arrived_departure_time, pick_up_passenger_time, pick_up_passenger_longitude, pick_up_passenger_latitude, passenger_getoff_time, passenger_getoff_longitude, passenger_getoff_latitude, cancel_time, cancel_operator, cancel_type_code, drive_mile, drive_time, order_status, price, gmt_create, gmt_modified
from order_info


        and id = #{id}


        and passenger_id = #{passengerId}


        and passenger_phone = #{passengerPhone}


        and driver_id = #{driverId}


        and driver_phone = #{driverPhone}


        and car_id = #{carId}


        and vehicle_type = #{vehicleType}


        and address = #{address}


        and order_time = #{orderTime}


        and depart_time = #{departTime}


        and departure = #{departure}


        and dep_longitude = #{depLongitude}


        and dep_latitude = #{depLatitude}


        and destination = #{destination}


        and dest_longitude = #{destLongitude}


        and dest_latitude = #{destLatitude}


        and encrypt = #{encrypt}


        and fare_type = #{fareType}


        and fare_version = #{fareVersion}


        and receive_order_car_longitude = #{receiveOrderCarLongitude}


        and receive_order_car_latitude = #{receiveOrderCarLatitude}


        and receive_order_time = #{receiveOrderTime}


        and license_id = #{licenseId}


        and vehicle_no = #{vehicleNo}


        and to_pick_up_passenger_time = #{toPickUpPassengerTime}


        and to_pick_up_passenger_longitude = #{toPickUpPassengerLongitude}


        and to_pick_up_passenger_latitude = #{toPickUpPassengerLatitude}


        and to_pick_up_passenger_address = #{toPickUpPassengerAddress}


        and driver_arrived_departure_time = #{driverArrivedDepartureTime}


        and pick_up_passenger_time = #{pickUpPassengerTime}


        and pick_up_passenger_longitude = #{pickUpPassengerLongitude}


        and pick_up_passenger_latitude = #{pickUpPassengerLatitude}


        and passenger_getoff_time = #{passengerGetoffTime}


        and passenger_getoff_longitude = #{passengerGetoffLongitude}


        and passenger_getoff_latitude = #{passengerGetoffLatitude}


        and cancel_time = #{cancelTime}


        and cancel_operator = #{cancelOperator}


        and cancel_type_code = #{cancelTypeCode}


        and drive_mile = #{driveMile}


        and drive_time = #{driveTime}


        and order_status = #{orderStatus}


        and price = #{price}


        and gmt_create = #{gmtCreate}


        and gmt_modified = #{gmtModified}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from order_info


        and id = #{id}


        and passenger_id = #{passengerId}


        and passenger_phone = #{passengerPhone}


        and driver_id = #{driverId}


